import React from 'react';
import logo from './logo.svg';
import './App.css';
import styled from 'styled-components'
import 'bootstrap/dist/css/bootstrap.min.css';

import { Container, Row, Col } from 'reactstrap';
import { Jumbotron } from 'reactstrap';

const Logo = styled.div`
  font-size: 1.5em;
`;

const ControlButton = styled.div`
`;

const AppLayout = styled.div`
  padding: 40px;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
`;

var bgColors = { "Default": "#81b71a",
                    "Blue": "#00B1E1",
                    "Cyan": "#37BC9B",
                    "Green": "#8CC152",
                    "Red": "#E9573F",
                    "Yellow": "#F6BB42",
};

function Sections(props) {
  return (
  <Jumbotron className="m-0">
    <Container>
      <Row>
        <Col style={{textAlign: "center"}} xs="12" sm="6">
          <img src={props.imgurl} alt={props.imgtitle} />
        </Col>
        <Col xs="12" sm="6">
          <p>{props.title}</p>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec tincidunt ipsum augue. In faucibus vehicula magna pulvinar aliquam. Cras aliquam feugiat lorem non auctor. Quisque sed loreem egstas mauris veneenatis commodo u id nibh. Ut porta libero sed semper faucibus.</p>
        </Col>
      </Row>
    </Container>
  </Jumbotron>
  );
}

export default class App extends React.Component {
  render() {
    return (
      <div>
        <Sections color="bgColors.Yellow" imgurl="https://i.vimeocdn.com/video/595198868_505x160.jpg" imgtitle="Monsoon III" title="Monsoon III" />
        <Sections color="bgColors.Yellow" imgurl="https://i.vimeocdn.com/video/590587169_530x315.jpg" imgtitle="Move 2" title="Move 2" />
      </div>
    );
  }
};
